import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';

//function App() {
const Guess = ({ onGuess }) =>{
  const [text, setText] = useState('Click me');
  const [displayText, setDisplayText] = useState(''); 
  const handleButtonClick =() => {
    setText('I was clicked');
  }
  const handleInputChange = (e) => {
    setDisplayText(e.target.value);
  };
  const [guess, setGuess] = useState('');
  const handleGuess = () => {
    onGuess(guess);
    setGuess('');
  };


  const [value, setValue] = useState(0); // 使用useState来创建变量和更新函数

  const incrementValue = () => {
    setValue(value + 1);
  };

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>

        <h1>Display Box</h1>
      <p>Current Value: {value}</p>
      <button onClick={incrementValue}>Increment Value</button>

        <output id="output">{displayText}</output>




        <input
        type="text"
        placeholder="输入要显示的字符"
        value={displayText}
        onChange={handleInputChange}
      />
      <input
        type="text"
        placeholder="input a letter to guess"
        value={guess}
        onChange={(e) => setGuess(e.target.value)}
      />
       <button onClick={handleGuess}>Guess</button>

      /*<button onClick={handleButtonClick}>{text}</button>*/

      </header>
    </div>
  );
}


export default Guess;
