import logo from './wof.jpg';
import './App.css';
import React, { useState, useEffect } from 'react';

function App() {
  let phrase = "Have a great day";
  const [hiddenPhrase, setHiddenPhrase] = useState('');
  const [inputLetter, setInputLetter] = useState('');

  const handleInputChange = (event) => {
    setInputLetter(event.target.value);
  };

  const decryptString = (encryptedString, plainText, letter) => {
    return plainText.split('').map((char, index) => {
      return char === letter ? encryptedString[index] : char;
    }).join('');
  };


  const handleGuess = () => {
    const decryptedString = decryptString(hiddenPhrase, phrase, inputLetter);
    setHiddenPhrase(decryptedString);
  };

  useEffect(() => {
    // 在组件挂载时将 phrase 变为 hiddenPhrase
    const tmpPhrase= phrase.replace(/[a-zA-Z]/g, '*');
    setHiddenPhrase(tmpPhrase);
  }, []); // 空数组表示只在组件挂载时执行一次

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <h1>Wheel Of Fortune</h1>
        <p>Hidden Phrase: {hiddenPhrase}</p>
        <input
          type="text"
          placeholder="input a letter to guess"
          value={inputLetter} // 将输入框的值与状态变量关联
          onChange={handleInputChange} // 指定输入变化时的处理函数
        />
        <button onClick={handleGuess}>Guess</button>
      </header>
    </div>
  );
}

export default App;
