import logo from './wof.jpg';
import './App.css';
import React, { useState, useEffect } from 'react';

function App() {
  let phrase = "Have a great day";
  const [hiddenPhrase, setHiddenPhrase] = useState('');
  const [inputLetter, setInputLetter] = useState('');

  const handleInputChange = (event) => {
    setInputLetter(event.target.value);
  };

  const decryptString = (encryptedString, plainText, letter) => {
    const isCorrectGuess = plainText.toLowerCase().includes(letter.toLowerCase());

    if (isCorrectGuess) {
      const updatedHiddenPhrase = Array.from(hiddenPhrase); // Update displayed phrase
      phrase.split('').forEach((char, index) => {
        if (char.toLowerCase() === letter.toLowerCase()) {
          updatedHiddenPhrase[index] = char;
        }
      });

      setHiddenPhrase(updatedHiddenPhrase.join(''));

      if (!updatedHiddenPhrase.includes('*')) {
        setCorrectGuess(true);
      }
    }
  };


  const handleGuess = () => {
    console.log(hiddenPhrase);
    console.log(phrase);
    console.log(inputLetter);
    const decryptedString = decryptString(hiddenPhrase, phrase, inputLetter);
    console.log(decryptedString);
    setHiddenPhrase(decryptedString);
  };

  useEffect(() => {
    // 在组件挂载时将 phrase 变为 hiddenPhrase
    const tmpPhrase= phrase.replace(/[a-zA-Z]/g, '*');
    setHiddenPhrase(tmpPhrase);
  }, []); // 空数组表示只在组件挂载时执行一次

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <h1>Wheel Of Fortune</h1>
        <p>Hidden Phrase: {hiddenPhrase}</p>
        <input
          type="text"
          placeholder="input a letter to guess"
          value={inputLetter} // 将输入框的值与状态变量关联
          onChange={handleInputChange} // 指定输入变化时的处理函数
        />
        {correctGuess && <p>Congratulations! You guessed the word correctly!</p>}
        <button onClick={handleGuess}>Guess</button>
      </header>
    </div>
  );
}

export default App;
