import React, { useState } from 'react';

const HistoryScoreWindow = (googleId) => {
    const [historyScores, setHistoryScores] = useState([]); // 示例历史分数
    const [allPlayerScores, setAllPlayerScores] = useState([]); // 示例所有玩家分数


    const handleDeleteScore = (index) => {
        setHistoryScores(prevScores => prevScores.filter((_, i) => i !== index));
    };

    function findUserInfoByGoogleId() {
        const url = `https://wheeloffortune-68830.ue.r.appspot.com/api/UserInfo/findUserInfoByGoogleId?googleId=${user.uid}`;
        console.log(url);
        axios.get(url)
          .then(response => {
            console.log("response.data=",response.data);
            setUserInfo(response.data);
          })
          .catch(error => {
            setError(error.message);
            console.log("error.message= ",error.message);
            setShowUserName(true);
            setShowGame(false);
            console.log("error");
          });
    };

    return (
        <div>
            <h2>History Scores</h2>
            <ul>
                {historyScores.map((item, index) => (
                    <li key={index}>{item.userName}: {item.score} <button onClick={() => handleDeleteScore(index)}>Delete</button></li>
                ))}
            </ul>

            <h2>All Player Scores</h2>
            <ul>
                {allPlayerScores.map((item, index) => (
                    <li key={index}>{item.userName}: {item.score}</li>
                ))}
            </ul>
        </div>
    );
};

export default HistoryScoreWindow;
