import React, { useState } from 'react';

const HistoryScoreWindow = () => {
    const [historyScores, setHistoryScores] = useState([{ userName: 'Alice', score: 100 }, { userName: 'Bob', score: 85 }]); // 示例历史分数
    const [allPlayerScores, setAllPlayerScores] = useState([{ userName: 'Charlie', score: 95 }, { userName: 'Diana', score: 90 }]); // 示例所有玩家分数
    const [currentUserName, setCurrentUserName] = useState('John Doe'); // 当前用户名
    const [newUserName, setNewUserName] = useState(''); // 新用户名输入

    const handleDeleteScore = (index) => {
        setHistoryScores(prevScores => prevScores.filter((_, i) => i !== index));
    };

    const handleChangeName = () => {
        setCurrentUserName(newUserName);
        setNewUserName('');
    };

    const handleReturnToGame = () => {
        // 返回游戏的逻辑
    };

    return (
        <div>
            <h2>History Scores</h2>
            <ul>
                {historyScores.map((item, index) => (
                    <li key={index}>{item.userName}: {item.score} <button onClick={() => handleDeleteScore(index)}>Delete</button></li>
                ))}
            </ul>

            <h2>All Player Scores</h2>
            <ul>
                {allPlayerScores.map((item, index) => (
                    <li key={index}>{item.userName}: {item.score}</li>
                ))}
            </ul>

            <div>
                <h3>Current User: {currentUserName}</h3>
                <input type="text" value={newUserName} onChange={(e) => setNewUserName(e.target.value)} placeholder="Enter new name" />
                <button onClick={handleChangeName}>Commit</button>
            </div>

            <button onClick={handleReturnToGame}>Return to Game</button>
        </div>
    );
};

export default HistoryScoreWindow;
